var link=document.querySelector(".write-us"),popup=document.querySelector(".modal-feedback"),close=document.querySelector(".modal-feedback__close"),overlay=document.querySelector(".modal-overlay"),form=popup.querySelector("form"),username=popup.querySelector("[name=name]"),email=popup.querySelector("[name=email]"),button=popup.querySelector(".btn"),l_user=localStorage.getItem("username"),l_email=localStorage.getItem("email");link.addEventListener("click",function(e){e.preventDefault(),overlay.classList.add("popup-show"),popup.classList.add("modal-show"),username.focus()}),close.addEventListener("click",function(e){e.preventDefault(),overlay.classList.remove("popup-show"),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),overlay.classList.remove("popup-show"),popup.classList.remove("modal-error"))}),overlay.addEventListener("click",function(){popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),overlay.classList.remove("popup-show"),popup.classList.remove("modal-error"))}),form.addEventListener("submit",function(e){username.value&&email.value?(localStorage.setItem("username",username.value),localStorage.setItem("email",email.value)):(e.preventDefault(),popup.classList.add("modal-error"))});
